import { KeyboardAvoidingView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native'
import React from 'react'

const Login = () => {
     return (
          <KeyboardAvoidingView style={styles.container} behavior="padding">

               <View style={styles.inputContainer}>
                    <TextInput
                         placeholder='Email'
                         // value={ }
                         // onChangeText={txt => }
                         style={styles.input}
                    />
                    <TextInput
                         placeholder='Password'
                         // value={ }
                         // onChangeText={txt => }
                         style={styles.input}
                         secureTextEntry
                    />

                    <View style={styles.buttonContainer}>
                         <TouchableOpacity
                              // onPress={handleLogin}
                              style={styles.button}
                         >
                              <Text style={styles.buttonText}>Login</Text>
                         </TouchableOpacity>
                         <TouchableOpacity
                              // onPress={handleSignUp}
                              style={[styles.button, styles.buttonOutline]}
                         >
                              <Text style={styles.buttonOutlineText}>Register</Text>
                         </TouchableOpacity>
                    </View>


               </View>

          </KeyboardAvoidingView>
     )
}

export default Login

const styles = StyleSheet.create({
     container: {
          flex: 1,
          justifyContent: 'center',
          alignItems: 'center',
     },
     inputContainer: {
          width: '80%'
     },
     input: {
          backgroundColor: 'white',
          paddingHorizontal: 15,
          paddingVertical: 10,
          borderRadius: 10,
          marginTop: 5,
     },
     buttonContainer: {
          width: '60%',
          justifyContent: 'center',
          alignItems: 'center',
          marginTop: 40,
     },
     button: {
          backgroundColor: '#0782F9',
          width: '100%',
          padding: 15,
          borderRadius: 10,
          alignItems: 'center',
     },
     buttonOutline: {
          backgroundColor: 'white',
          marginTop: 5,
          borderColor: '#0782F9',
          borderWidth: 2,
     },
     buttonText: {
          color: 'white',
          fontWeight: '700',
          fontSize: 16,
     },
     buttonOutlineText: {
          color: '#0782F9',
          fontWeight: '700',
          fontSize: 16,
     },

});
